mapper.select:
select id, password, name, email, phoneNumber, authority, enabled, temporaryPassword
from user
where id=#{user_id}

select count(*)
from user
where id = #{id}

SELECT id, password, name, email, phoneNumber
FROM user
WHERE email = #{email}

SELECT count(*)
FROM user
WHERE id = #{id} and email = #{email}

mapper.update:
UPDATE USER
SET temporaryPassword = #{temporaryPassword}
WHERE id = #{id}

mapper.select:
SELECT temporaryPassword
FROM user
WHERE id = #{id}

mapper.update:
UPDATE USER
SET password = #{password}
WHERE id = #{id}
