mapper.select:
SELECT bno, title, writer, content, regDate, viewCnt
FROM board
where bno = #{bno}

mapper.update:
UPDATE board
SET title = #{title},
	  content = #{content}
WHERE bno = #{bno}

mapper.select:
SELECT org_file_name, stored_file_name
FROM file
WHERE file_no = #{file_no}

mapper.update:
UPDATE file
SET del_dv = 'Y'
WHERE file_no = #{file_no}
