mapper.select:
SELECT id, password, email, phoneNumber, gender, image 
FROM user
where id = #{id}

SELECT count(id)
FROM user
WHERE id = #{id}

mapper.update:
UPDATE user
SET password = #{password}, 
    email = #{email},
    phoneNumber = #{phoneNumber},
    image = #{image}
WHERE id = #{id}

mapper.select:
SELECT id, password
FROM user
WHERE id = #{id}

SELECT count(*)
FROM user
WHERE id = #{id} AND email = #{email}

mapper.update:
UPDATE USER
SET password = #{password}
WHERE id = #{id}
